export interface ActivateAccountLocalization {
    /**
     * strings in activate account page
     */
    activateAccount: {
        /**
         * Activate account page title
         */
        title: string;
        /**
         * New password input label
         */
        newPasswordInputLabel: string;
        /**
         * String displayed as placeholder when new password field is empty
         */
        newPasswordInputPlaceholder: string;
        /**
         * Error displayed when new password is empty
         */
        newPasswordIsRequired: string;
        /**
         * Confirm password input label
         */
        confirmPasswordInputLabel: string;
        /**
         * String displayed as placeholder when confirm password field is empty
         */
        confirmPasswordInputPlaceholder: string;
        /**
         * Error displayed when confirm password is empty
         */
        confirmPasswordIsRequired: string;
        /**
         * Error displayed when passwords does not match
         */
        confirmPasswordMustMatch: string;
        /**
         * Error displayed when didn't agree to terms and conditions
         */
        confirmTermsAndConditionsIsRequired: string;
        /**
         * Active account submit button text
         */
        activateButton: string;
        /**
         * Title to be displayed if activate account succeeded
         */
        successTitle: string;
        /**
         * Message to be displayed if activate account succeeded
         */
        successMessage: string;
        /**
         * Error displayed if link is invalid as title
         */
        linkFailedTitle: string;
        /**
         * Error displayed if link is invalid as description
         * @deprecated not in use anymore because we show the error directly from the API response
         */
        linkFailedMessage: string;
        /**
         * Error displayed if link is expired as title
         * @deprecated not in use anymore because we show the error directly from the API response
         */
        linkExpiredTitle: string;
        /**
         * Error displayed if link is expired as description
         * @deprecated not in use anymore because we show the error directly from the API response
         */
        linkExpiredMessage: string;
        /**
         * Title to be displayed if activate account failed
         */
        failedTitle: string;
        /**
         * Description message to be displayed if activate account failed
         */
        failedMessage: string;
        /**
         * Back to login button text in activate account failed section
         */
        failedBackToLoginButton: string;
        /**
         * Consts for terms and conditions & privacy for activate account page
         */
        disclaimerText: string;
        disclaimerCheckboxLabel: string;
        disclaimerTextRequired: string;
        termsLinkText: string;
        termsLink: string;
        privacyLinkText: string;
        privacyLink: string;
        termsAndPrivacyConjunctionText: string;
    };
}
