export interface ForgetPasswordLocalization {
    /**
     * strings in forget password page
     */
    forgetPassword: {
        /**
         * Forget password page title
         */
        title: string;
        /**
         * Text displayed in forget password form before email input
         */
        description: string;
        /**
         * Forget password form email input label
         */
        emailInputLabel: string;
        /**
         * Forget password form email input placeholder
         */
        emailInputPlaceholder: string;
        /**
         * error message displayed if email is invalid
         */
        emailMustBeValid: string;
        /**
         * error message displayed if email is empty
         */
        emailIsRequired: string;
        submitButtonText: string;
        backToLogin: string;
        resetEmailSentTitle: string;
        resetEmailSentMessage: string;
    };
}
