export interface ResetPasswordLocalization {
    /**
     * strings in forget password page
     */
    resetPassword: {
        /**
         * Reset password page title
         */
        title: string;
        /**
         * Text displayed in reset password form before password inputs
         */
        description: string;
        /**
         * New password input label
         */
        newPasswordInputLabel: string;
        /**
         * String displayed as placeholder when new password field is empty
         */
        newPasswordInputPlaceholder: string;
        /**
         * Error displayed when new password is empty
         */
        newPasswordIsRequired: string;
        /**
         * Confirm password input label
         */
        confirmPasswordInputLabel: string;
        /**
         * String displayed as placeholder when confirm password field is empty
         */
        confirmPasswordInputPlaceholder: string;
        /**
         * Error displayed when confirm password is empty
         */
        confirmPasswordIsRequired: string;
        /**
         * Error displayed when passwords does not match
         */
        confirmPasswordMustMatch: string;
        /**
         * Reset password submit button text
         */
        resetPasswordButton: string;
        /**
         * Title to be displayed if reset password succeeded
         */
        successTitle: string;
        /**
         * Message to be displayed if reset password succeeded
         */
        successMessage: string;
        /**
         * Title to be displayed if reset password failed
         */
        failedTitle: string;
        /**
         * Description message to be displayed if reset password failed
         */
        failedMessage: string;
        /**
         * Back to login button text in reset passwrod failed section
         */
        failedBackToLoginButton: string;
    };
}
